mapper.select:

    inner join follow f on f.fwing_no = u.uno
		where f.fwer_no=#{no}
		order by f.cdt desc


inner join follow f on f.fwer_no = u.uno
where f.fwing_no=#{no}
order by f.cdt desc

mapper.update:
update user
nick = #{nickname}, pwd = password(#{password}), hp_url = #{homepageURL}, gh_url = #{githubURL}, in_url = #{instarURL}, tw_url = #{twitterURL}, tech = #{tech}, bio = #{bio}, photo = #{photo}
where uno = #{no}

update user set
blocked = 1
where uno=#{reportedUser.no}

update user set
state = 0
where uno=#{no}
